import os
import time

ready_signal_path = "/tmp/consumer_ready.signal"
data_signal_path = "/tmp/data_available.signal"
data_file_path = "/tmp/data.txt"

def wait_for_consumer_ready():
    print("Waiting for consumer to be ready...")
    while not os.path.exists(ready_signal_path):
        time.sleep(1)
    print("Consumer ready detected.")

def send_data(data):
    with open(data_file_path, 'a') as f:
        for item in data:
            f.write(item + "\n" )
    print("Data written to file.")

def signal_data_available():
    with open(data_signal_path, 'w') as f:
        f.write("Data available")
    print("Data available signal created.")

def send_stop_signal():
    stop_signal = "Stop"
    with open(data_signal_path, 'a') as f:
        f.write(stop_signal + "\n")
    print("Stop signal sent.")

if __name__ == '__main__':

    wait_for_consumer_ready(ready_signal_path)

    for i in range(30):
        data = [
            "",
            "",
            ""];
        send_data(data_file_path, data)
        signal_data_available(data_signal_path)

    send_stop_signal(data_file_path)
