# src/main/sender.py

import subprocess
import os
import json
import struct
import time
import numpy as np

def sender(matrix):
    # Signal to the receiver to initialize the function
    signal_message = "__INITIALIZE_FUNCTION__"
    #print(matrix)

    # Convert the signal message to a JSON string
    signal_json = json.dumps(signal_message)

    receiver_path = os.path.abspath('../NLP/receiver.py')

    # Run the receiver as a subprocess and pass the signal message
    subprocess.run(['python', receiver_path], input=signal_json, text=True, check=True)

    # Now, send the 2D matrix


    # Convert the matrix to a JSON string
    json_string = json.dumps(matrix)

    # Run the receiver again and pass the JSON-encoded matrix
    for i in range(1,3):
        subprocess.run(['python', receiver_path], input=json_string, text=True, check=True)
        time.sleep(1)


if __name__ == '__main__':
    matrix = ["", "", ""];
    sender(matrix)